import React from "react";
import ReactDom from "react-dom";
import Button from './Button'
import "./styles.css"

function soma(a, b) {
    alert("A soma é = {a + b}")
}


function App() {


    return(
        <div className="App">
            <h1>Hello World</h1>
            <Button onClick={()=> soma(10, 20)} name="Somar" />
        </div>
    )
}

const rootElement = document.getElementById('root')
ReactDom.render(<App />, rootElement)
